// EditCampus.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

// ... (imports and other code)

const EditCampus = () => {
  const { id } = useParams(); 
  const [campusData, setCampusData] = useState({
    name: '',
    address: '',
    description: ''
  });

  // Fetch campus data based on the ID
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`/api/campus/${id}`); 
        if (response.ok) {
          const data = await response.json();
          setCampusData(data);
        }
      } catch (error) {
        console.error('Error fetching campus data:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch(`/api/campus/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(campusData),
      });
      if (response.ok) {
        // Handle success, maybe redirect or show a success message
      }
    } catch (error) {
      console.error('Error updating campus data:', error);
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setCampusData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div>
      <h1>Edit Campus</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={campusData.name}
            onChange={handleChange} 
          />
        </label>
        <br />
        <label>
          Address:
          <input
            type="text"
            name="address"
            value={campusData.address}
            onChange={handleChange} 
          />
        </label>
        <br />
        <label>
          Description:
          <textarea
            name="description"
            value={campusData.description}
            onChange={handleChange} 
          ></textarea>
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default EditCampus;
