import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import EditCampus from '../views//EditCampus';
import { editCampusThunk } from '../../store/thunks';

const EditCampusContainer = ({ campus, editCampus, history }) => {
  const handleEdit = editedCampusData => {
    editCampus(editedCampusData);
  };

  useEffect(() => {
    // Fetch initial campus data or campus based on URL params
    // Example: fetchCampus(campusId);
  }, []);

  return (
    <div>
      <h1>Edit Campus</h1>
      <EditCampus campus={campus} handleEdit={handleEdit} />
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  // Extract campus data based on URL params or from Redux state
  // Example: const campusId = ownProps.match.params.id;
  // Return the specific campus object from the Redux state
  return {
    campus: state.campuses.find(campus => campus.id === ownProps.match.params.id)
  };
};

const mapDispatchToProps = dispatch => {
  return {
    editCampus: campus => dispatch(editCampusThunk(campus))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(EditCampusContainer);
