import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(() => ({
  formContainer: {
    width: '500px',
    backgroundColor: '#f0f0f5',
    borderRadius: '5px',
    margin: 'auto',
  },
  formTitle: {
    backgroundColor: '#c5c8d6',
    marginBottom: '15px',
    textAlign: 'center',
    borderRadius: '5px 5px 0px 0px',
    padding: '3px',
  },
}));

const EditCampus = (props) => {
  const { campusData, handleInputChange, handleSubmit } = props;
  const classes = useStyles();

  const [editedCampus, setEditedCampus] = useState(campusData);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedCampus({ ...editedCampus, [name]: value });
    handleInputChange(name, value); // Optionally handle the change in parent/container
  };

  return (
    <div>
      <h1>Edit Campus</h1>

      <div className={classes.root}>
        <div className={classes.formContainer}>
          <div className={classes.formTitle}>
            <Typography style={{ fontWeight: 'bold', fontFamily: 'Courier, sans-serif', fontSize: '20px', color: '#11153e' }}>
              Edit a Campus
            </Typography>
          </div>
          <form style={{ textAlign: 'center' }} onSubmit={(e) => handleSubmit(e, editedCampus)}>
            <label style={{ color: '#11153e', fontWeight: 'bold' }}>Name: </label>
            <input type="text" name="name" value={editedCampus.name} onChange={handleChange} />
            <br />
            <br />

            <label style={{ color: '#11153e', fontWeight: 'bold' }}>Address: </label>
            <input type="text" name="address" value={editedCampus.address} onChange={handleChange} />
            <br />
            <br />

            <label style={{ color: '#11153e', fontWeight: 'bold' }}>Description: </label>
            <input type="text" name="description" value={editedCampus.description} onChange={handleChange} />
            <br />
            <br />

            <label style={{ color: '#11153e', fontWeight: 'bold' }}>Image URL: </label>
            <input type="text" name="imageUrl" value={editedCampus.imageUrl} onChange={handleChange} />
            <br />
            <br />

            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
            <br />
            <br />
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditCampus;
