/*==================================================
CampusView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display a single campus and its students (if any).
================================================== */
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
// Take in props data to construct the component

const CampusView = (props) => {
  const { campus, deleteCampus, editCampus, deleteStudent } = props;

  const handleDelete = () => {
    deleteCampus(campus.id);
    console.log("Campus deleted");
  };

  return (
    <div>
      <h1>{campus.name}</h1>
      <p>{campus.address}</p>
      <p>{campus.description}</p>

      <img src="https://static01.nyt.com/images/2020/03/14/upshot/14up-colleges-remote/14up-colleges-remote-videoSixteenByNineJumbo1600.jpg" alt="Campus" />

      <div>
        <Link to={`/editcampus`}>
          <button>Edit campus</button>
        </Link>
      </div>

      <div>
        <button onClick={() => deleteCampus(campus.id)}>Delete College</button>
        <hr />
      </div>

      <Link to={`/newstudent`}>
        <button>Enroll New Student</button>
      </Link>

      <p>Total Students: {totalStudents}</p>
      {campus.students.map((student) => {
        let name = student.firstname + " " + student.lastname;
        return (
          <div key={student.id}>
            <Link to={`/student/${student.id}`}>
              <h2>{name}</h2>
            </Link>
            <button onClick={() => deleteStudent(student.id)}>Delete</button>
          </div>
        );
      })}
    </div>
  );
};

CampusView.propTypes = {
  campus: PropTypes.object.isRequired,
  deleteCampus: PropTypes.func.isRequired,
  editCampus: PropTypes.func.isRequired,
  deleteStudent: PropTypes.func.isRequired,
};

export default CampusView;
