import React from 'react';
import { useState } from 'react';
import Button from '@material-ui/core/Button';

const EditCampus = ({ campus, handleEdit }) => {
  const [editedCampus, setEditedCampus] = useState({
    name: campus.name,
    address: campus.address,
    description: campus.description
    // other fields if any
  });

  const handleChange = event => {
    const { name, value } = event.target;
    setEditedCampus(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = event => {
    event.preventDefault();
    handleEdit(editedCampus); // Send edited campus data to parent/container
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name:</label>
      <input type="text" name="name" value={editedCampus.name} onChange={handleChange} />
      <label>Address:</label>
      <input type="text" name="address" value={editedCampus.address} onChange={handleChange} />
      <label>Description:</label>
      <input type="text" name="description" value={editedCampus.description} onChange={handleChange} />
      {/* Other input fields if any */}
      <Button variant="contained" color="primary" type="submit">
        Submit
      </Button>
    </form>
  );
};

export default EditCampus;
