/*==================================================
/src/store/reducers/campus.js

This is a Reducer function that accepts 2 parameters: the previous state object (aka current state) and an action object. 
Depending on the Action object, the Reducer updates the State and return the new State object.
It also defines the State and its default initial value.
================================================== */
import * as at from "../actions/actionTypes"; // Import Action Types ("at" keyword for Action Type)

const initialState = {
  campuses: [], // Empty campuses array
};

const campus = (state = initialState, action) => {
  switch (action.type) {
    case at.FETCH_ALL_CAMPUSES:
      return {
        ...state,
        campuses: action.payload,
      };
    case at.ADD_CAMPUS:
      return {
        ...state,
        campuses: [...state.campuses, action.payload],
      };
    case at.DELETE_CAMPUS:
      return {
        ...state,
        campuses: state.campuses.filter(campus => campus.id !== action.payload),
      };
    case at.EDIT_CAMPUS:
      return {
        ...state,
        campuses: state.campuses.map(campusItem => {
          return campusItem.id === action.payload.id ? action.payload : campusItem;
        }),
      };
    default:
      return state;
  }
};

export default campus;
